var angular = require('angular');


/**
 * Configuration script for the main portal application.
 *
 * This script sets up the resolvers for the sync managers used to manage:
 *
 * - workorders
 * - workflows
 * - workflow results
 *
 * @param $stateProvider
 * @param $urlRouterProvider
 * @constructor
 */
function AppConfig($stateProvider, $urlRouterProvider) {
  $urlRouterProvider.otherwise('/workorders/list');

  $stateProvider
    .state('app', {
      abstract: true,
      templateUrl: 'app/main.tpl.html',
      data: {
        columns: 3
      },
      resolve: {
         profileData: function () {
          return {
            "id": "rkX1fdSH",
            "username": "trever",
            "name": "Trever Smith",
            "position": "Senior Truck Driver",
            "phone": "2657258272",
            "email": "trever@wfm.com",
            "notes": "Trever doesn't work during the weekends.",
            "avatar": "https://s3.amazonaws.com/uifaces/faces/twitter/kolage/128.jpg",
            "banner": "http://web18.streamhoster.com/pentonmedia/beefmagazine.com/TreverStockyards_38371.jpg",
            "password": "sha1:10000::10c2bb710af17f75b73faf35d33874587b44bd3017e02a87a5bff57621d7a019558a660fc4a9ed476745db9812f27199fa0f8ffc3ea2dc3c5bca08913abd967c"
          };
        }
      },
      controller: function($scope, $state, $mdSidenav, Auth) {
        $scope.$state = $state;
        $scope.toggleSidenav = function(event, menuId) {
          $mdSidenav(menuId).toggle();
          event.stopPropagation();
        };
        $scope.navigateTo = function(state, params) {
          if (state) {
            if ($mdSidenav('left').isOpen()) {
              $mdSidenav('left').close();
            }
            $state.go(state, params);
          }
        };
        $scope.logout = function() {
          if (Auth.keycloak) {
            Auth.keycloak.logout();
          }
        };
      }
    });
}


angular.module('app').config(["$stateProvider", "$urlRouterProvider", AppConfig]);
